package com.yendregi.stardate.util;

import java.awt.Color;
import java.awt.Component;
import java.awt.Dimension;
import java.awt.Font;
import java.awt.Graphics;
import java.awt.Image;
import java.awt.image.BufferedImage;
import java.io.ByteArrayInputStream;
import java.io.IOException;
import java.util.Base64;

import javax.imageio.ImageIO;
import javax.swing.JButton;
import javax.swing.JOptionPane;
import javax.swing.JPanel;

/**
 * Custom JOptionPane class to display a custom about dialog
 * @author Andrzej K. W.
 *
 */
public class AboutStarDatePane extends JOptionPane {

	private static final long serialVersionUID = -3052578877127454349L;

	private static final String panelImageB64_1 = "";
	private static final String panelImageB64_2 = "";
	private static final String panelImageB64_3 = "";
	private static final String panelImageB64_4 = "";
	private static final String panelImageB64_5 = "";
	private static final String panelImageB64_6 = "";
	private static final String panelImageB64_7 = "";
	private static final String panelImageB64_8 = "";
	private static final String panelImageB64_9 = "";
	private static final String panelImageB64_10 = "";
	private static final String panelImageB64_11 = "";
	private static final String panelImageB64_12 = "";
	private static final String panelImageB64_13 = "";
	private static final String panelImageB64_14 = "";
	private static final String panelImageB64_15 = "";
	private static final String panelImageB64_16 = "";
	
	private String aboutString = null;
	private Image panelImage = null;
	
	/**
	 * Main Constructor
	 * 
	 * @param messageString
	 */
    public AboutStarDatePane(String messageString) {    	
		super(messageString);
		this.aboutString = messageString;
		this.panelImage = getPanelImage();
		this.hideButtons();		
	}

	/**
	 * The overridden paint class to first render initial content then override with custom stuff
	 */
    @Override
    public void paint(Graphics g) {    
		
		//super.paint(g); //ignore calling the inner elements to render them selves as we override the content
    	
		Dimension prefSize = this.getPreferredSize();

        g.drawImage(this.panelImage, 0, 0, (int)prefSize.getWidth(), (int)prefSize.getHeight(), null);
    	
    	int textYOffset = 25;
    	int textXOffset = 70;    	
    	double pixPerCharAve = 7.2;
    	
    	g.setColor(new Color(239,222,5));
    	g.setFont(new Font("Tahoma", Font.BOLD, 14));
    	
    	String[] aboutStrings = this.aboutString.split("\n");
    	for(int i = 0; i < aboutStrings.length; i++){
    		textXOffset = (int)prefSize.getWidth()/2 - (int)((double)aboutStrings[i].length()*pixPerCharAve/2.0);
    		g.drawString(aboutStrings[i], textXOffset, (i+1)*textYOffset);
    	}
    	
    }

    private void hideButtons() {
    	Component[] internalComponents = this.getComponents();    	
    	for(int i = 0; i<internalComponents.length; i++) {    		
    		if( internalComponents[i] instanceof JPanel){
    			JPanel inner = (javax.swing.JPanel)internalComponents[i];
    			Component[] innerComponents = inner.getComponents();
    			for(int j = 0; j<innerComponents.length; j++) {
    				if(innerComponents[j] instanceof JButton){
    					((JButton)innerComponents[j]).setVisible(false);
    				}
    			}
    		}
    	}
    }
    
    private Image getPanelImage() {
    	BufferedImage image = null;
    	byte[] imageByte;
    	String decodeStr = panelImageB64_1 + panelImageB64_2 + panelImageB64_3 + panelImageB64_4;
    	decodeStr += panelImageB64_5 + panelImageB64_6 + panelImageB64_7 + panelImageB64_8;
    	decodeStr += panelImageB64_9 + panelImageB64_10 + panelImageB64_11 + panelImageB64_12;
    	decodeStr += panelImageB64_13 + panelImageB64_14 + panelImageB64_15 + panelImageB64_16;
    	imageByte = Base64.getDecoder().decode(decodeStr);
    	ByteArrayInputStream bis = new ByteArrayInputStream(imageByte);
    	try {
			image = ImageIO.read(bis);
	    	bis.close();
		} catch (IOException e) { }
    	return image.getScaledInstance(-1,-1, Image.SCALE_FAST);
    }
}
